#!/bin/bash\nwhile true; do\n  clear\n  echo "Running main.py..."\n  python3 main.py > app.log 2>&1\n  STATUS=$?\n  TIMESTAMP=$(date "+%Y-%m-%d %H:%M:%S")\n\n  if [ $STATUS -ne 0 ]; then\n    echo "Crash detected at $TIMESTAMP"\n    tail -n 10 app.log\n    echo "Saving crash log..."\n    {\n      echo "---- DEBUG REPORT ($TIMESTAMP) ----"\n      tail -n 20 app.log\n      echo "----------------------------------"\n    } >> debug_report.txt\n    echo "Restarting in 5s..."\n    sleep 5\n  else\n    echo "App ran successfully at $TIMESTAMP"\n    git fetch origin main > /dev/null 2>&1\n    git pull --rebase > git_error.log 2>&1\n    if ! git diff --quiet; then\n      git add . && git commit -m "Auto-fix at $TIMESTAMP" > /dev/null 2>&1\n      git push origin main > git_error.log 2>&1 && echo "Pushed to GitHub"\n    fi\n    inotifywait -e modify,create,delete -r . >/dev/null 2>&1\n  fi\ndone
